function result = mq_grid(grid_X, grid_Y, vevtor_TargetFunc)num_Row = size(grid_X, 1);num_Col = size(grid_X, 2);num_Term = size(vevtor_TargetFunc, 1);result = zeros(num_Col, num_Row);for i = 1:num_Term    temp = vevtor_TargetFunc(i,1)*ones(num_Row, num_Col);    for j = 1:vevtor_TargetFunc(i,2)        temp = temp.*grid_X;    end    for j = 1:vevtor_TargetFunc(i,3)        temp = temp.*grid_Y;    end    result = result + temp;endend